import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { Listing } from '../types';
import ListingCard from '../components/ListingCard';
import { fetchWithAuth } from '../utils/api';

const Purchases: React.FC = () => {
  const { user } = useAuth();
  const [purchases, setPurchases] = useState<Listing[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchPurchases = async () => {
      try {
        const response = await fetchWithAuth('/listings/purchases');
        const data = await response.json();
        setPurchases(data);
      } catch (err) {
        setError('Failed to load purchases');
        console.error('Error fetching purchases:', err);
      } finally {
        setLoading(false);
      }
    };

    if (user) {
      fetchPurchases();
    }
  }, [user]);

  if (!user) {
    return <div className="container mx-auto p-4">Please log in to view your purchases</div>;
  }

  if (loading) {
    return <div className="container mx-auto p-4">Loading...</div>;
  }

  if (error) {
    return <div className="container mx-auto p-4 text-red-500">{error}</div>;
  }

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">My Purchases</h1>
      {purchases.length === 0 ? (
        <p>You haven't made any purchases yet.</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {purchases.map((listing) => (
            <ListingCard
              key={listing.id}
              listing={listing}
              onDelete={() => {}}
              isDeleting={false}
            />
          ))}
        </div>
      )}
    </div>
  );
};

export default Purchases;
